mapper.select:
select t.id          as "id",
       t.user_name   as "userName",
       t.name        as "name",
       t.password    as "password",
       t.create_time as "createTime",
       t.update_time as "updateTime"
  from t_user t

  		and t.id=#{params.id}
  		and t.user_name=#{params.userName}
  		and t.name=#{params.name}
  		and t.password=#{params.password}
  		and t.id in 
  			(select ur.user_id from t_user_role ur where ur.role_id =#{params.roleId})
